package Rivewclass1;

public class StringConcat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String num1="10";
		int num2=10;
		double num3=10;
		System.out.println(num1+(num2+num3));
		System.out.println(num1+num2+num3);
		System.out.println(num3+num2+num1);
		
	

	}

}
