public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte num=(byte)129;
		short number2=125;
		byte num2=(byte)number2;
		
		System.out.println(num);
		System.out.println(num2);
		System.out.println((int) 10.5);
		

	}

}
