package com.syntax.class9;

public class PrintingPatterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// deceler number row and culuam this way we used nasted for loop one loo use for culom one for row
		
		for(int i=1; i<=4; i++) {
			for(int j=1; j<=5; j++) {
				
				System.out.print(" * ");
				
			}
			System.out.println();
		}
		
		// Square 10/ 10
		
		System.out.println("********************");
		
		for(int i=1; i<=10; i++) {
			for(int j=1; j<=10; j++) {
				
				System.out.print(" * ");
				
			}
			System.out.println();
			
		
	}

}}
