package com.sysntex.class3;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1=14;
		int number2=3;
		System.out.println(number1/number2);
		System.out.println(number1%number2);
		
		int var1=100;
		var1++;
		System.out.println(var1);
		
		int var2=-100;
		var2--;
		System.out.println(var2);
		
		int number4=-100;
		number4++;
		System.out.println(number4);
		number4--;
		
		double number5=10;
		number5++;
		System.out.println(number5++);
		System.out.println(++number5);
		
		System.out.println("************************");
		
		double number8=20;
		number8+=10;
		System.out.println(number8);
		number8-=40;
		System.out.println(number8);
		
		System.out.println("************************");
		
		number8=100;
		number8*=4;
		System.out.println(number8);
		
		System.out.println("************************");
		
		number8=100;
		number8/=4;
		System.out.println(number8);
		
		System.out.println("************************");
		
		number8=100;
		number8%=4;
		System.out.println(number8);
		
		
		
		
		
	}

}
