public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * write a java program to add subtract multiply and divide 2 decimal values. you program should say.
		 * The __of 2 numbers ___and___is equal to___"
		 */
		
		double number1=10.50;
		double number2=10.50;
		double sum=number1+number2;
		double sub=number1-number2;
		double mul=number1*number2;
		double div=number1/number2;
		
		System.out.println(" The addition of 2 numbers "+number1+ " and " +number2+ " is equal to "+sum);
		System.out.println(" The addition of 2 numbers "+number1+ " and " +number2+ " is equal to "+sub);
		System.out.println(" The addition of 2 numbers "+number1+ " and " +number2+ " is equal to "+mul);
		System.out.println(" The addition of 2 numbers "+number1+ " and " +number2+ " is equal to "+div);

	}

}
